import React, {Component} from 'react';
import HeroImage from '../elements/HeroImage/HeroImage';
import { API_URL, API_KEY, IMAGE_BASE_URL, POSTER_SIZE, BACKDROP_SIZE} from '../../config';
import SearchBar from '../elements/SearchBar/SearchBar';
import FourColGrid from '../elements/FourColGrid/FourColGrid';
import MovieThumb from '../elements/MovieThumb/MovieThumb';
import LoadMoreBtn from '../elements/LoadMoreBtn/LoadMoreBtn';
import Spinner from '../elements/Spinner/Spinner';

import './Home.css';

class Home extends Component {
    state = {
        movies: [],
        heroImage: null,
        loading: false,
        currentPage: 0,
        totalPages: 0,
        searchTerm: ''
    }


    componentDidMount()  {
        this.setState({ loading: true});
        // specifiera url från vart vi hämtar data
        const endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=1}`;
        // Hämtar data
        this.fetchItems(endpoint);
    }


    loaMoreItems = () => {
        let endpoint = '';
        this.setState({loading: true});

        //om search fältet är tom visa populära filmer
        if (this.state.searchTerm === ''){
            endpoint = `${API_URL}movie/popular?api_key=${API_KEY}&language=en-US&page=${this.state.currentPage + 1}}`;
        } else {
            endpoint = `${API_URL}search/movie?api_key=${API_KEY}&language=en-US&query${this.state.searchTerm}&page=${this.state.currentPage + 1}`;
        }
        this.fetchItems(endpoint);
    }






    fetchItems = (endpoint) => {
        //hämtar data med fetch(). fetch returnar 'promise'. Promise är nått som kommer resolvas i framtiden, vi kan vänta att den ska resolvas med then()
        fetch(endpoint)
        //convertera resultat till json 
        .then(result => result.json())

        .then(result => {
            this.setState({
                movies: [...movies, ...result.results],
                heroImage: heroImage || result.results[0],
                loading: false,
                currentPage: result.page,
                totalPages: result.total_pages
              })
        })
        .catch(error => console.error('Error:', error))
    }




//class somponent user render -> return method
    render(){
        return(
            <div className="rmbd-home">
                {/* kolla om heroIMage finns, om ja visa denna om inte visa null */}

                <div>
                <HeroImage image={`${IMAGE_BASE_URL}${BACKDROP_SIZE}${this.state.heroImage.backdrop_path}`}
                title={this.state.heroImage.original_title}
                text={this.state.heroImage.overview}/>
                <SearchBar />
                </div> 


                <FourColGrid />
                <Spinner />
                <LoadMoreBtn />
            </div>
        )
    }
}

export default Home;
